# Die thrown multiple times 
# 2025-12-14
r = ''
# Separate the characters 
sl <- strsplit(r, split = "")[[1]] |> as.numeric()
# How many times tossed? 
(lt = length(sl))
# Frequency table of values 
(tb=table(sl))
# Expected Average 
lt/6
# Average 
mean(tb)
# Barplot of the frequency table 
barplot(tb)

